import{r as e,h as n,F as s,d as a}from"./p-8Q-8j7RM.js";import{t as i}from"./p-DnVJQNAc.js";const t=":host{display:block}";const r=8;const d=class{constructor(n){e(this,n)}render(){return n(s,{key:"eefc77896e2281695a9cd02a932a2c8333a39eaa"},n("app-header",{key:"c79981c120039d0cc74c7cea38fdc697304d07af",headerTitle:i[r]}),n("ion-content",{key:"c2960f9e01cb35619ff34cf23b3a91c3127d664b",class:"ion-padding",id:"main-menu"},n("app-content",{key:"d5f9df374d7e8d3ffcfbffa3fa56c01ac3168b55"},n("h1",{key:"9effd20530e3e89e5bcac7ed0382fb3a2cd22cf5",class:"ion-hide-md-down"},i[r]),n("p",{key:"1f2391da0337b5f5dd11e12e61debe22a08a5295",class:"lead"},"«In Basel habe ich den Judenstaat gegründet»: ",n("br",{key:"dcb09496eddc387d06c3d8d1fceaa4b2602fe696"}),"Basel als Gastgeber der Zionistenkongresse"),n("p",{key:"a30e7cd1fbaa69101f55ce937eb37e2238e5109d"},"Eigentlich sollte der erste Zionistenkongress gar nicht in Basel sondern in München stattfinden. Weil sich die jüdische Gemeinde der bayrischen Hauptstadt aber skeptisch zeigte, musste ein anderer Ort gesucht werden. Vom 29. bis 31. August 1897 trafen sich 200 Personen aus ganz Europa zum ersten Zionistenkongress in Basel. Die Basler Regierung hatte bei der Organisation mitgeholfen, in dem sie die Burgvogtei in Kleinbasel und den Musiksaal im Stadtcasino für Veranstaltungen zur Verfügung gestellt hatte. Auch die Bevölkerung reagierte überwiegend mit Sympathie. Der Zionismus, die Schaffung «einer gesicherten Heimstätte für das jüdische Volk in Palästina», die Theodor Herzl am ersten Kongress proklamiert hatte, stiess insbesondere in den pietistischen Kreisen der ‹Freunde Israels› auf Unterstützung. Am Schluss des ersten Kongresses 1897 dankte Theodor Herzl der «gastlichen Stadt» Basel, «die uns mit solchem Wohlwollen empfangen hat». Einige Tage nach dem Kongress schrieb er in sein Tagebuch: «In Basel habe ich den Judenstaat gegründet. Wenn ich das heute laut sagte, würde mir ein universelles Gelächter antworten. In fünf Jahren, jedenfalls in fünfzig, wird es Jeder einsehen»."),n("p",{key:"27857785a7a9ef709515d1338697f8054fed6a28"},"Die Basler Juden beeindruckte die Idee der Auswanderung nach Palästina hingegen wenig. Sie fühlten sich hier zuhause und spürten wenig Drang, die Heimat zu verlassen. Für den Zionistenkongress 1901 sandte die Basler Regierung erstmals ein offizielles Begrüssungsschreiben und empfing eine Delegation mit Herzl an der Spitze. Der freisinnige Regierungspräsident Heinrich David äusserte, der Zionismus sei «etwas Erhabenes», und er freute sich, «dass diese grosse, schöne Idee in Basel ein Heim gefunden hat». Die Kongresse dürften dazu beigetragen haben, dass sich die jüdische und die nicht-jüdische Bevölkerung von Basel näherkamen."),n("p",{key:"f554f2879a9274e04a6c35d8ec6414cece5818cc"},"Bis 1911 fanden sieben von zehn Zionistenkongressen in Basel statt. Danach verlor Basel als Austragungsort an Bedeutung. Ein zehntes und letztes Mal begrüsste Basel 1946 Gäste des Kongresses."),n("h2",{key:"9815314d847f15992b364a5aca1d3915cfdf4c20"},"Rätsel"),n("p",{key:"9875a06ff3cc9d8d8ee35450929058055094c655"},"In welchem Hotel übernachtete Theodor Herzl während des ersten Zionistenkongresses in Basel?"),n("app-video",{key:"9b4c92ff557ae3dd09ef8aa89d6ee5d769c12779"},n("iframe",{key:"28fa079314d1d45a669eb741cc80cad695954b21",width:"100%",height:"100%",style:{"aspect-ratio":"16/9"},src:"https://www.youtube.com/embed/Sst6hx7WznY",title:"YouTube video player",frameborder:"0",allow:"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share",allowfullScreen:true}),n("span",{key:"18adf0015b27a073e3cf4127fb844b50501ede0b",slot:"caption"},"Vom 29. bis 31. August 2022 jährte sich der Erste Zionistenkongress in Basel zum 125. Mal. Dort legte der Initiator Theodor Herzl den Grundstein für die Gründung Israels. Anlässlich des Jubiläums beleuchtete die Universität Basel mit Prof. Dr. Erik Petry die Spuren, die Herzl in Basel hinterlassen hat.")),n("p",{key:"b34f4f3f2702a01ed50efe83b73de132f558768c"},n("ion-button",{key:"1567fd538b4acb55f2ff34baa4ad520d1ac1f596",color:"primary",onClick:e=>this.failModal.open()},"Hotel Jura"),n("ion-button",{key:"6862bb9620401d40d899c011c6d28be0eb3be98c",color:"primary",onClick:e=>this.failModal.open()},"Hotel Bellevue"),n("ion-button",{key:"e9949b18fc91780e0595117ddd76809887553171",color:"primary",onClick:e=>this.successModal.open()},"Hotel Drei Könige")),n("app-literatur",{key:"c3c313ccc24ec89c3a8b2001b39a23ded673eeac",items:[n(s,null,"Guth Biasini, Nadia: Basel und der Zionistenkongress, in Haumann, Heiko (Hg.): Der Erste Zionistenkongress von 1897 – Ursachen, Bedeutung, Aktualität, Basel 1997, S. 131-140."),n(s,null,"Haumann, Heiko (Hg.): Der Erste Zionistenkongress von 1897 – Ursachen, Bedeutung, Aktualität, Basel 1997."),n(s,null,"Haumann, Heiko: Juden in Basel und Umgebung – Zur Geschichte einer Minderheit. Darstellung und Quellen für den Gebrauch an Schulen. Herausgegeben von den Regierungsräten der Kantone Basel-Stadt und Basel-Landschaft. Basel 1999.")]}),n("app-dialog-fail",{key:"4623f09f6aa03f9e932a991104124331deaa0431",ref:e=>this.failModal=e,titleTxt:"Leider falsch"},n("p",{key:"3d6d8612687c0362ed0691677a56768987e3887b"},"Die richtige Antwort ist «Hotel Drei Könige».")),n("app-dialog-success",{key:"ba9d9b53ab234fc64285750b116632c0a0c9961b",nr:r,ref:e=>this.successModal=e},n("ion-img",{key:"4dec1c6f226af9b3781a3773865855f31cbe3f9d",src:a("../../assets/hurray.gif")}),n("div",{key:"2ab1de26e52b1b9e8bf36b6b44faa71deca7d09d",class:"ion-padding"},"Du hast Kapitel «",i[r],"» erfolgreich abgeschlossen !!")))),n("app-footer",{key:"74d6846775fc8b77c1cead385b8413314bb053a5"}))}};d.style=t;export{d as page_teil_8};
