import{r as e,h as a,d as n,F as i}from"./p-8Q-8j7RM.js";import{t as r}from"./p-DnVJQNAc.js";const t=":host{display:block}";const s=5;const d=class{constructor(a){e(this,a)}render(){return a(i,{key:"d518fc19fe5fc98990acf687540e1d9140425419"},a("app-header",{key:"da265d473e87da6e2c800916d908b2a81dffc797",headerTitle:r[s]}),a("ion-content",{key:"0332ffe4220cdb7239fe8fb5428cc70b62201faf",class:"ion-padding",id:"main-menu"},a("app-content",{key:"d32f65b3efcb507197d5be91a730d4d00489c688"},a("h1",{key:"2a65a155534e46ed2c74de892d9b13719e5a79ab",class:"ion-hide-md-down"},r[s]),a("p",{key:"5177f56c87c6dad2cad8a4a001937af27cfc363c",class:"lead"},"Die Synagoge an der Leimenstrasse"),a("p",{key:"c9bf0ef2395b951e34b41b78937c264bea3c6455"},"Die Mitglieder der dritten jüdischen Gemeinde, die sich ab etwa 1805 gebildet hatte, trafen sich für mehrere Jahrzehnte in Privatwohnungen zum Gebet. 1850 nahm die erste Synagoge am Unteren Heuberg den Betrieb auf. Die kleine Synagoge befand sich in einer umgebauten Liegenschaft. 16 Jahre später nahm Hermann Gauss die Planung einer «richtigen» Synagoge in Angriff. Der deutsche Architekt war 1858 als Angestellter des technischen Büros der Schweizerischen Centralbahn nach Basel gekommen und hatte sich zwei Jahre später selbständig gemacht. Gauss liess sich bei der Planung des Kuppelbaus vom orientalischen Stil der Synagogen von Dresden und Stuttgart inspirieren."," "),a("app-img",{key:"1f025a2fa335eb47468cfbf14fe14f0c365779f2",src:"../../assets/teil-5/synagoge-1.png"},"Die von Architekt Hermann Gauss gebaute Synagoge an der Leimenstrasse war mit seinem Kuppelbau ein architektonischer Farbtupfer in der Stadt. Die Aufnahme entstand zwischen 1868 und 1880.",a("br",{key:"60b01b712c9d845437ef2a1be26ac2a29b2da990"}),"Quelle: Staatsarchiv Basel-Stadt, SMM Inv.2016.1.17.3"),a("p",{key:"e10ea3d426ed7ff65031c747c675712d65f7873d"},"Bei der Eröffnung der Synagoge an der Ecke Leimenstrasse/Eulerstrasse am 9. September 1868 war der Vorsteher des Basler Justizdepartements und spätere freisinnige Bundesrat Ernst Brenner Ehrengast. Zwei Jahrzehnte später beschlossen die Mitglieder der Israelitischen Gemeinde Basel eine Vergrösserung der Synagoge als Reaktion auf das Anwachsen der jüdischen Gemeinde. Die Kapazität wurde von 300 auf 700 Plätze erhöht. Architekt Paul Reber hatte die am 15. September 1892 eröffnete erweiterte Synagoge um eine zweite Kuppel und einen Langbau ergänzt."," "),a("app-img",{key:"83805726a0624491bf1a1c876f3fbcb0450f1861",src:"../../assets/teil-5/der-israelit.png"},"Kurze Nachricht in der Zeitschrift «Der Israelit» vom 30. September 1868."),a("p",{key:"0df1b2086c0f11630d34e652da3da3c2f9492616"},"Paul Reber (1835-1908) war nicht nur Architekt, sondern auch Politiker. Er sass mehrere Jahrzehnte im Grossen Rat und im Bürgerrat. Politisch liess er sich nicht klar verorten, er pendelte zwischen einer freisinnigen und einer liberal-konservativen Haltung. Wenige Jahre bevor er die Synagoge umbaute, hatte er in unmittelbarer Nachbarschaft die katholische Marienkirche in neuromanischem Stil gebaut. Reber hatte sich mit zahlreichen Kirchenbauten in der ganzen Schweiz einen Namen gemacht. In erster Linie baute Reber zwar protestantische Kirchen, die ‹National-Zeitung› schrieb in einem Nachruf am 31. Oktober 1908 aber: «Er diente mit seinem schönen Können den Protestanten, Katholiken und Israeliten». Die Wahl von Paul Reber als Architekt des Synagogenumbaus war ein Zeichen der israeltischen Gemeinde an das protestantische Basel, dass man sich als Teil der Stadt verstand."),a("app-img",{key:"b9c26db76434aee33315f41401f140f385aef4f9",src:"../../assets/teil-5/synagoge-2.png"},"Die 1892 von Architekt Paul Reber erweiterte Synagoge wurde mit einer zweiten Kuppel und einem Langbau ergänzt. Die Aufnahme entstand im Zeitraum nach der Eröffnung bis 1911.",a("br",{key:"ee6f5d343f8f2e5d190af391f061540ad6c9cad9"}),"Quelle: StaBS NEG A 608"),a("h2",{key:"70cb82078b2293b548945ac58dc09c9f93d2b84f"},"Rätsel"),a("p",{key:"0d801eb3d81db8c22ce51c7f62d591faa8c7cc3c"},"Die Konsuln welcher Staaten nahmen 1868 an der Einweihung der Synagoge teil?"),a("p",{key:"c85f17acd590c45697a192dad1090156c27d9bd0"},a("ion-button",{key:"f4d2cb4af284cd8a3e49bfc5a23bff5385f93aec",color:"primary",onClick:e=>this.failModal.open()},"Deutschland und England"),a("ion-button",{key:"ecf845925fdd7c4505d97f28301e877f04f57088",color:"primary",onClick:e=>this.successModal.open()},"Nordamerika und Frankreich"),a("ion-button",{key:"0180f1d90441cb359af9305ee7d0950331c7dbd6",color:"primary",onClick:e=>this.failModal.open()},"Italien und Österreich")),a("app-literatur",{key:"4da0edca6777d163c33618ac1c53e916498ade2a",items:[a(i,null,"Guth-Dreyfus Katja: 175 Jahre Israelitische Gemeinde Basel. Basler Stadtbuch 1980. S. 153-162."),a(i,null,"Guth, Nadia: Synagoge und Juden in Basel. Basel 1988."),a(i,null,"Haumann, Heiko (Hg.): Acht Jahrhunderte Juden in Basel, Basel 2005.")]}))),a("app-dialog-fail",{key:"c1c6c35ba94f4cb83aa31a683af854aecf0e3ad5",ref:e=>this.failModal=e,titleTxt:"Leider falsch"},a("p",{key:"6b952c82c37d9ec21ca23f22c6b51182042aca1e"},"Die richtige Antwort ist «Nordamerika und Frankreich»")),a("app-dialog-success",{key:"cb0ca02d9602c8758363eeba3eb817db85f429ef",nr:s,ref:e=>this.successModal=e},a("ion-img",{key:"98d5b5a8a4d133a54683d5dda3a6b58ef9bd035a",src:n("../../assets/hurray.gif")}),a("div",{key:"10970f8ad0114c38c65d597383332d96bb7f38a0",class:"ion-padding"},"Du hast Kapitel «",r[s],"» erfolgreich abgeschlossen !!")))}};d.style=t;export{d as page_teil_5};
