import{r as e,h as a,d as i,F as n}from"./p-8Q-8j7RM.js";import{t as s}from"./p-DnVJQNAc.js";const r=":host{display:block}";const t=9;const d=class{constructor(a){e(this,a)}render(){return a(n,{key:"bb50ec22083479ed9fca7a6b6ade5ee9a1ff22a2"},a("app-header",{key:"3994b02fcf594a9459c33abbce0ab8cf59ed1d98",headerTitle:s[t]}),a("ion-content",{key:"1d6781584374a1fb667008181e6297b6112190b6",class:"ion-padding",id:"main-menu"},a("app-content",{key:"d5286f70009c6ad25df6190b374d55af91c6c775"},a("h1",{key:"f36134fd8ffb3f50a8c7102d65c8f90028ca24a6",class:"ion-hide-md-down"},s[t]),a("p",{key:"5ec308785a0adefc55e69241a9c3ace3a29f0cef",class:"lead"},"Der Trödler aus dem Elsass: ",a("br",{key:"dc4b75ef5751c7761040f989278e1907385c7ead"}),"Isaac Ségal (1831-1916) wandert von Hégenheim nach Basel ein"),a("p",{key:"741773f9af0e41157c924d46629a109e7bf6d9f8"},"Isaac Ségal kam am 27. April 1831 in Hégenheim zur Welt. Rund dreissig Jahre später zog er nach Basel und soll hier 1862 sein Geschäft für Antiquitäten gegründet haben. Offiziell gemeldet in der Stadt war Isaac Ségal aber erst seit dem 27. März 1867. Das Basler Adressbuch von 1868 listet Ségal als Trödler an der Schützenmattstrasse 10 auf. Er versorgte die Quartierbewohnerinnen und -bewohner mit Antiquitäten und Möbeln des alltäglichen Gebrauchs. Nach der Hochzeit mit Rebecca Guggenheim (1841-1906) zog das Ehepaar an den Spalenberg. Isaac Ségal unterstützte das 1874 in Hégenheim gegründete israelitische Alters- und Pflegeheim jahrelang finanziell."," "),a("app-img",{key:"bd909095507cf0b6416bb95469dbcf736c86f4af",style:{"max-width":"500px"},src:"../../../assets/teil-9/berthold-segal.jpg"},"Berthold Ségal kam 1873 auf die Welt und erhielt 1904 das Basler Bürgerrecht. Bild: Privatarchiv Antiquités Ségal&Selig."),a("p",{key:"43ce90cb075bb2c09d966b886d23365fc6ed04eb"},"Sein 1873 geborener Sohn Berthold Ségal erhielt 1904 das Basler Bürgerrecht. Ségal Junior war Mitglied des israelitischen Männervereins Dowor Tow und vertrat diesen als Delegierter in der Armenkommission der Israelitischen Gemeinde Basel. Spätestens nach dem Tod von Isaac Ségal am 6. Oktober 1916 hatte Berthold Ségal das Geschäft übernommen. Er spezialisierte das Antiquariat, das sich inzwischen am Fischmarkt befand, auf wertvolle Antiquitäten. Unterstützung erhielt er von seiner Frau Betty, geborene Kaufmann. In den Jahren nach dem Ersten Weltkrieg betreute Ségal als Experte Auktionen von teilweise grossen Sammlungen, die aus wirtschaftlichen Gründen verkauft werden mussten."," "),a("p",{key:"7852e648db16619fb314d3b28fd65bbe9a66ccf2"},"Das Antiquariat begann in den letzten Jahren des 19. Jahrhunderts enge Geschäftsbeziehungen zu Museen aufzunehmen. Das Historische Museum Basel etwa erwarb bei Ségal mehrfach Objekte für die eigene Sammlung. Ein erster Kauf – ein englisches Rahm- und Milchkännchen sowie ein Teller – ging 1896 über die Bühne."),a("h2",{key:"5bd1c08055e66069124067cc491314167f10f97c"},"Rätsel"),a("p",{key:"58e974d55f151f08cb95587bcc8d830c7d94bc55"},"In der Sendung «Sternstunde Religion» des Schweizer Fernsehen vom 27. Februar 2005 sprach der Soziologe Ralph Weill – anlässlich des 200-Jahr-Jubiläums der Israelitischen Gemeinde Basel (IGB) – über die 800-jährige jüdische Geschichte von Basel. Ab Minute 28:00 erzählt Weill, dass die Region zwischen Jura, Vogesen und Schwarzwald früher eine einheitliche Kulturregion mit einer einheitlichen jüdischen Gemeinschaft gewesen sei. Diese teile sich nun in drei sehr verschiedene Gruppen auf: Eine elsässische, ausschliesslich französisch sprechende Gruppe mit vielen aus Nordafrika eingewanderten Juden, eine gemischte Gruppe in Basel mit einem elässisch-badischen und osteuropäischen Hintergrund. Auch in Südbaden gebe es wieder eine jüdische Bevölkerung."),a("p",{key:"a3d7fc2e4cca7bc3ff6e0764e181b7bc1ea38583"},"Welche Herkunft haben diese südbadischen Juden?"),a("app-video",{key:"a0541160941e67ee1ae04acf5a31c523d317cbef"},a("iframe",{key:"52a71e8b501ff092b815eb842dfc72b88b526891",width:"100%",height:"100%",style:{"aspect-ratio":"16/9"},src:"https://www.srf.ch/play/embed?urn=urn:srf:video:e5f5d2ef-2f26-440d-80fd-1a28dea23d58&startTime=1692&subdivisions=false",allowFullScreen:true,allow:"geolocation *; autoplay; encrypted-media"}),a("span",{key:"efb0322bb67422886235fdf1cb6ada19642af5e4",slot:"caption"},"«Sternstunde Religion» des Schweizer Fernsehen mit Ralph Weill vom 27. Februar 2005")),a("p",{key:"ecbcad830afe7ce3c0baed30ca87c94d6f6e9056"},a("ion-button",{key:"25f05bcc38ee39aac3044862a6db71b9849bb9f6",color:"primary",onClick:e=>this.failModal.open()},"Ungarisch"),a("ion-button",{key:"fa94c8ede2a1faf1716bedda4e9108e400a3371d",color:"primary",onClick:e=>this.successModal.open()},"Russisch"),a("ion-button",{key:"df5f8f1703f0108262b0747d4fda27f9db9bd36d",color:"primary",onClick:e=>this.failModal.open()},"Amerikanisch")))),a("app-dialog-fail",{key:"93e9d33e506abf9f164844f2ae890efcfc641e50",ref:e=>this.failModal=e,titleTxt:"Leider falsch"},a("p",{key:"84b46f6478567f3c4152243a9985c2a77647e96e"},"Die richtige Antwort ist «Russisch»")),a("app-dialog-success",{key:"68a54f4a54573b2517b33a16d04c942aadebb3c8",nr:t,ref:e=>this.successModal=e},a("ion-img",{key:"0e1c42b6cd4741890f779ab50f2b350814a11ec5",src:i("../../assets/hurray.gif")}),a("div",{key:"7017b60a45e464792fa2267156007c81d4f16692",class:"ion-padding"},"Du hast Kapitel «",s[t],"» erfolgreich abgeschlossen !!")))}};d.style=r;export{d as page_teil_9};
