import{r as e,h as n,d as i,F as d}from"./p-8Q-8j7RM.js";import{t as a}from"./p-DnVJQNAc.js";const s=":host{display:block}";const r=12;const t=class{constructor(n){e(this,n)}render(){return n(d,{key:"dfda7a6f6bf96dcac7d8892d4ade91a3868262b4"},n("app-header",{key:"aca898d5d802695f1d9be058ed843f8195824af6",headerTitle:a[r]}),n("ion-content",{key:"f82081b100eb126fab4041b4cdbbc3c653cbd66f",class:"ion-padding",id:"main-menu"},n("app-content",{key:"4b3f6141da7767de6ec91b76cd39739e708b57eb"},n("h1",{key:"6b11af5ca702ac0481410a43fdc74171f7ce949c",class:"ion-hide-md-down"},a[r]),n("p",{key:"053bbe0d3d21236dbe9645933671db855a76afc1",class:"lead"},"Öffentlich-rechtlich anerkannt: ",n("br",{key:"d0261619f6e823d044f70fe8507bf4b5f17542d1"}),"Die Entwicklung der jüdischen Bevölkerung bis heute"),n("p",{key:"2851b2b86c37e92d64b9181cabc10b13673b80f9"},"Das Wachstum der jüdischen Bevölkerung in Basel ging auch im 20. Jahrhundert vorerst weiter. Von über 2400 Personen 1910 stieg die Zahl bis auf rund 3000 Menschen während des Zweiten Weltkriegs. Die jüdische Gemeinde in Basel kümmerte sich um zahlreiche jüdische Flüchtlinge, die ab 1933 aus dem nationalsozialistischen Deutschland nach Basel geflohen waren. In den Jahrzehnten nach Kriegsende verringerte sich die jüdische Bevölkerung in Basel hingegen kontinuierlich. Bis 1980 ging die Zahl auf etwa 2000 Jüdinnen und Juden zurück und halbierte sich bis 2020 auf 906 Personen. Gemäss Zahlen des Statistischen Amtes Basel-Stadt stieg die Anzahl 2021 wieder leicht auf 961 Personen an."),n("p",{key:"6f2df4049106bca034e505317163fc2a452e28c0"},"Eine Untersuchung der Wohnorte der Mitglieder der jüdischen Gemeinde zeigt auf, dass sich 1982 im Vergleich zu 1910 die Wohnorte etwas stärker in die Quartiere Gotthelf, Bachletten und St. Alban verschoben hatten. Vermehrt waren Jüdinnen und Juden zudem in die Vororte der Stadt gezogen, insbesondere in die Gemeinden Allschwil und Binningen. Der Autor Simon Stern stellte fest, dass zwar vermehrt Wohnorte in den äusseren Quartieren lagen, die meisten Jüdinnen und Juden aber weiterhin in der Umgebung des Zentrums um die Synagoge lebten."),n("app-img",{key:"d7ae28bb74364ecbf348a18e44fc8e0874c9a239",src:"../../assets/teil-12/karte.jpg"},"Die Karte (Stern, S. 123) zeigt die Wohnorte der Mitglieder der jüdischen Gemeinde Basel von 1910 und 1982 im Vergleich. Die Wohnorte verschoben sich vom Zentrum leicht in die äusseren Quartiere."),n("p",{key:"3ebf8b1671dcd00917ffaf4d45d830e9cd6ef5a9"},"Die Israelitische Gemeinde Basel (IGB) erhielt 1973 als erste jüdische Gemeinde der Schweiz die öffentlich-rechtliche Anerkennung. Damit wurde sie den christlichen Landeskirchen gleichgestellt. Die IGB deckt nur einen Teil der jüdischen Bevölkerung Basels ab, ist aber die mit Abstand grösste jüdische Gemeinde. 1927 hatte sich die orthodoxe Israelitische Religionsgemeinschaft (IRG) von der IGB abgespaltet und sich als privatrechtlicher Verein mit einer eigenen Synagoge an der Ahornstrasse konstituiert. 2004 gründeten liberale Juden den Verein Migwan. Das Spektrum innerhalb der jüdischen Bevölkerung umfasst heute mehrere Gemeinden und Gruppen mit liberaler, religiöser und orthodoxer Ausrichtung."),n("h2",{key:"e9a8236852b56176cfcb33427d22c76fba00a959"},"Stimmt folgende Aussage?"),n("p",{key:"f26f6a9e4ce62fffd3c9e2fe24ff1cbfcc1282c1"},"Die beiden Gemeinden Allschwil und Binningen befindet sich näher an den jüdischen Wohnorten in Basel als Bottmingen und Reinach."),n("p",{key:"107db06156f7c9389d7908fd103d38e76d30849c"},n("ion-button",{key:"145f4351f921e8b13b8215414df9510e98868462",color:"primary",onClick:e=>this.successModal.open()},"Ja"),n("ion-button",{key:"ad3190a1746322e0685befa68048655e5196a3fc",color:"primary",onClick:e=>this.failModal.open()},"Nein")),n("app-literatur",{key:"c10002c80d41ca40feec0b1a408ce91c8ce0b3fc",items:[n(d,null,"Stern, Simon: Wohnplätze der Basler Juden 1910-1970, in: Regio Basiliensis 25/1984), Basel 1984. S. 119-127."),n(d,null,"Statistisches Jahrbuch des Kantons Basel-Stadt 2023, S. 25.")]}))),n("app-dialog-fail",{key:"54404c2591b166e30b3e21fb656ffbe91eaefbef",ref:e=>this.failModal=e,titleTxt:"Leider falsch"},n("p",{key:"354c8ba50ba5239adc4d3cc236166a8b3fda280b"},"Die richtige Antwort ist A")),n("app-dialog-success",{key:"07cae1d9a5bdfabc1955db8bee6bdd50e347af5d",nr:r,ref:e=>this.successModal=e},n("ion-img",{key:"b9bce6112f6d782728785892f545ea05f1f20c76",src:i("../../assets/hurray.gif")}),n("div",{key:"615ce0a9667b1dd2f3c1d7b0733633b36f2ec676",class:"ion-padding"},"Du hast Kapitel «",a[r],"» erfolgreich abgeschlossen !!")))}};t.style=s;export{t as page_teil_12};
