import{r as e,h as n,d as i,F as a}from"./p-D6EONAzC.js";import{t as r}from"./p-DnVJQNAc.js";const d=":host{display:block}";const t=4;const s=class{constructor(n){e(this,n)}render(){return n(a,{key:"70d97052020b4ad7465eff5998404ec47e45cb31"},n("app-header",{key:"26010a477b280941ae90115b1474a540628bef5f",headerTitle:r[t]}),n("ion-content",{key:"aebfc03756bf8ce55317fce7dd9e66dc6e28fe8c",class:"ion-padding",id:"main-menu"},n("app-content",{key:"53336158f46e7b560bb7935ceefe0f9d5f46592a"},n("h1",{key:"bb21d115d95ccb0157b6006eda5fea44717d27b1",class:"ion-hide-md-down"},r[t]),n("p",{key:"415f1d0ac339a4fb99e08def7a4636d66ea51b37",class:"lead"},"Zentren des jüdischen Lebens"),n("p",{key:"56b35d6dff67ba71ae30e19f5eafda62047c9155"},"Die Jüdinnen und Juden organisierten sich als religiöse Minderheit in einem eigenen Milieu mit Vereinen und Institutionen. Das soziale und kulturelle Leben fand in religiösen, fürsorgerischen, politischen und Sportvereinen statt. Dabei war die religiöse Identität wichtiger als die Zugehörigkeit zu einer bestimmten Schicht."),n("p",{key:"09aeb8fb711a10492214a8b0930cc38965ce78fc"},"In der Altstadt entstand am Unteren Heuberg ein erstes Zentrum jüdischen Lebens. Ein Warenlager wurde 1850 in eine kleine Synagoge umfunktioniert. Gründungsmitglieder des"," ",n("a",{key:"295fbc68d014db1f666ad2f4713c313523c27dc8",href:"https://www.geovistory.org/project/1719422/page/i1929590?p=1719422",target:"_blank",rel:"noopener noreferrer"},"Männervereins Dowor Tow")," ","wohnten in unmittelbarer Umgebung, an der Hutgasse, am Spalenberg oder an der Schützenmattstrasse. Mit dem Bau der Synagoge 1868 an der Leimenstrasse verlagerte sich das Zentrum zunehmend in dieses Quartier und ins Hegenheimerquartier."),n("app-img",{key:"d2c1536c93b63465811c95c27c2550a261bd7768",src:i("../../assets/teil-4/bild1.png")},"Quelle: StaBS BILD Schn. 142 ",n("br",{key:"b97780750421ee0dc50eeb7e24c87ed0d49d96ee"}),"Das Bild von Johann Jakob Schneider entstand 1879. Der Blick führt in den Unteren Heuberg zur alten Synagoge vom Gemsberg her."),n("app-img",{key:"0b1d8b4494b705d6b33d0c524e0a785ce3d23ba6",src:i("../../assets/teil-4/bild2.png")},"Quelle: StaBS NEG 1224 (Aufnahme von 1895)."),n("p",{key:"055e3c4067f13194118703a898825f6f77e0cf3a"},"Die Krankenpflege und die finanzielle Unterstützung von bedürftigen Mitgliedern und das Einhalten von Ritualen nach dem Tod bildeten die Kernaufgaben der jüdischen Frauen- und Männervereine. Einerseits ging es darum, die traditionelle Totenwache und Totenwaschung durchzuführen, andererseits Mitgliedern bei der Bestattung von Angehörigen unter die Arme zu greifen. Der Jüdische Frauenverein (1834), der Männerverein Dowor Tow (1857), der Verein Espérance für junge unverheiratete Männer (1866) und die Union israélite Chevroh Ez Chajim für junge unverheiratete Männer (1880) stellten sich diesen Aufgaben. Dem Frauenverein, dem ältesten jüdischen Verein in Basel, kam dabei eine spezielle Bedeutung zu, weil Frauen in den meisten Fällen den Haushalt führten und damit für die Einhaltung der religiösen Gesetze verantwortlich waren. Der Frauenverein engagierte sich auch in der Bildungsförderung und finanzierte um die Jahrhundertwende Ferienaufenthalte für arme jüdische Schulkinder."),n("p",{key:"5a39aa4757d2e87c2897e1b356d594b86d0bd5f7"},"1903 fand am Zionistenkongress ein internationales jüdisches Schauturnen statt, das auch die Basler Juden inspirierte. Aufbauend auf ersten zaghaften Versuchen gründete sich 1914 der ‹Jüdische Turnverein Basel›. Trainiert wurde in der Turnhalle des Leonhardschulhauses."),n("h2",{key:"9883fabec8e7808c0380abac33ba63bc2fe2e63b"},"Rätsel"),n("p",{key:"3e9b25b9e98145a2f07f2bc3f42b08cd8e93b3ca"},"Im Haus zum Schwarzen Ritter direkt hinter dem Gemsbrunnen betrieb ein Gründungsmitglied des Männervereins Dowor Tow ein Lithografie-Geschäft. Wie hiess der gesuchte Mann?"),n("p",{key:"0d8e29b48cc9091d1a8fb9aee1c216ca0e78b51c"},n("ion-button",{key:"7c4dfadcae6c9cd4ef4430ef78af7bdc44aa9313",color:"primary",onClick:e=>this.failModal.open()},"Arthur Wolf"),n("ion-button",{key:"81a9477610088a80f27f2b2fcca3882a9cfd2949",color:"primary",onClick:e=>this.successModal.open()},"Georg Wolf"),n("ion-button",{key:"8fa0227aa255bc375ab6ae2bb05b2e50bef361e6",color:"primary",onClick:e=>this.failModal.open()},"Emil Lévy")),n("app-literatur",{key:"6e24cca3b76fdab752915f81bdb7c6506f462695",items:[n(a,null,"Wecker, Regina: Zwischen Ökonomie und Ideologie. Arbeit im Lebenszusammenhang von Frauen im Kanton Basel-Stadt 1870-1910, Zürich 1997."),n(a,null,"Weill, Jeremy: 100 Jahre Jüdischer Turnverein Basel, Saarwellingen 2014.")]}),n("app-dialog-fail",{key:"c049899a5c6c713700aaaad5e8cc478c1b580784",ref:e=>this.failModal=e,titleTxt:"Leider falsch"},n("p",{key:"5d9ee399a2789de59534650e8e82efd2e43b706a"},"Die richtige Antwort ist «Georg Wolf».")),n("app-dialog-success",{key:"7830e1df8cdf9c73dcd22bd5ebb809ca1788355c",nr:t,ref:e=>this.successModal=e},n("ion-img",{key:"87ae448909d1281ae236f65f00e0ac81020ddf4e",src:i("../../assets/hurray.gif")}),n("div",{key:"85f6cb53775064a6712f5885f7e38e00598d8781",class:"ion-padding"},"Du hast Kapitel «",r[t],"» erfolgreich abgeschlossen !!")))),n("app-footer",{key:"9a8373f3ec6861bda5904b1eb02e7fee4e9e6883"}))}};s.style=d;export{s as page_teil_4};
