import{r as e,h as n,d as i,F as a}from"./p-D6EONAzC.js";import{t as r}from"./p-DnVJQNAc.js";const t=[{name:"Jüdische Handwerker und Israelit. Männerverein Dowor Tow",sparqlEndpoint:"https://sparql.geovistory.org/api_v1_project_1719422",query:`\n      PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>\n      PREFIX ontome: <https://ontome.net/ontology/>\n      PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>\n  PREFIX geov: <http://geovistory.org/resource/>\n      SELECT\n      (SAMPLE(?buildingLabel) as ?label)\n      (CONCAT(\n      '[',\n          GROUP_CONCAT(CONCAT('{"label":"',?persName,'", "url":"',str(?person),'?p=1719422"}'); separator=', '),\n          ']'\n      ) as ?children)\n\n      ?long ?lat ?link ?type\n      (count(?link) * 0.5 as ?radius)\n      WHERE {\n\n      # Construction -had presence-> Presence -was at-> Place (lat/long)\n      ?building ontome:p147i/ontome:p148 ?place.\n\n      # Construction -label-> label\n      ?building rdfs:label ?buildingLabel.\n\n      # Construction -is location of-> Legal location of an Actor\n      ?building ontome:p1851i ?location.\n\n      # -is social quality of -> Person\n      ?location ontome:p1411 ?person.\n\n      ?person ontome:p1188i/ontome:p1189 ?group.\n      ?group rdfs:label ?type.\n      FILTER(?group = geov:i1972234 || ?group = geov:i1929590).\n\n      # Person -label-> persName\n      ?person rdfs:label ?persName.\n\n      # Extract lat and long from WKT\n      bind(replace(str(?place), '<http://www.opengis.net/def/crs/EPSG/0/4326>', "", "i") as ?rep)\n      bind(xsd:float(replace(str(?rep), "^[^0-9\\\\.-]*([-]?[0-9\\\\.]+) .*$", "$1" )) as ?long )\n      bind(xsd:float(replace( str(?rep), "^.* ([-]?[0-9\\\\.]+)[^0-9\\\\.]*$", "$1" )) as ?lat )\n\n      # Append the project query param to the URI\n      bind(concat(str(?building), "?p=1719422") as ?link )\n\n\n      }\n      GROUP BY ?building ?long ?lat ?type ?link\n\n      `}];const s=":host{display:block}";const d=10;const o=class{constructor(n){e(this,n)}render(){return n(a,{key:"8c48a02107ab7e845ba50cd81342799075425b61"},n("app-header",{key:"9f353fbf8db4a39b65e7e70e5dc18b5dcb2d74f3",headerTitle:r[d]}),n("ion-content",{key:"1c9d9afdee1f5d8f7745591187b4d21440fcbd3b",id:"main-menu"},n("div",{key:"76f8e571fe700390e7af5c7ef734763d6a19b7ce",class:"ion-padding"},n("h1",{key:"5c5de8d4740d77ddc8884619f3e7bccde26a23df",class:"ion-hide-md-down"},r[d]),n("p",{key:"c3669fac27473da0fc6cbd226c9d51b74e5445cd",class:"lead"},"Ostjüdische Einwanderung und die Zentren der Wohnorte um 1910"),n("p",{key:"083c1d78566e401a405a6dec1ba44255bbceb38b"},"Ein erstes Zentrum jüdischen Lebens hatte sich in der Altstadt rund um den Unteren Heuberg gebildet, wo seit 1850 eine kleine Synagoge in Betrieb war. Mit dem Bau der grossen Synagoge 1868 an der Leimenstrasse entstand ein neuer Ort, der für viele Menschen zum neuen Lebensmittelpunkt wurde."," "),n("p",{key:"3f0337f13f82cc207bafefe1113b0e181fe44aeb"},"Um die Jahrhundertwende kamen viele osteuropäische Juden, die vor Pogromen geflüchtet waren, nach Basel. Für sie war die Stadt häufig eine Zwischenstation auf dem Weg nach Westen. Mehrere Tausend Ostjuden passierten ab 1906 Basel, wo sie sich oft einen oder mehrere Tage aufhielten. Einige osteuropäische Jüdinnen und Juden liessen sich in Basel nieder. 1910 lebten rund 450 Juden russischer und polnisch-russischer Herkunft im Kanton, was rund einem Fünftel der jüdischen Bevölkerung entsprach. Viele wohnten aufgrund der günstigen Mieten in Kleinbasel und eröffneten an der Florastrasse einen Gebetraum. Die osteuropäischen Juden unterschieden sich nicht nur kulturell, sondern auch mit ihren Berufen – überdurchschnittlich viele waren Handwerker – von den bereits ansässigen Juden. Deshalb stiessen sie bei vielen auf Skepsis."),n("p",{key:"420bab486e640b73a04abb049f81d26ae591466e"},"Der jüdische Frauenverein veröffentlichte um 1910 eine Liste mit jüdischen Handwerkerinnen und Handwerker. Die Lokalisierung der Wohnorte dieser Schuh- und Uhrenmacher, Näherinnen, Schneiderinnen, Coiffeusen und Papier- und Ölhändler macht Zentren jüdischen Lebens sichtbar. Die Wohnorte ballten sich in der Altstadt und im Hegenheimerquartier. Vereinzelte Handwerkerinnen und Handwerker wohnten im Kleinbasel."," "),n("p",{key:"1323d6b395d929a840cd34183b8d8be380e1c32b"},"Die jüdische Buchhandlung Goldschmidt, 1902 vom gebürtigen Litauer Pessach Meir Goldschmidt am Heuberg eröffnet, befand sich ebenso in der Grossbasler Altstadt wie die beiden koscheren Hotels und Restaurants von Mathilde Braunschweig-Ebstein (Hotel Kahn-Braunschweig) und Liselotte Simon-Wunderlin (Hotel Simon). Hier trafen sich die jüdischen Vereine zu Versammlungen und Sitzungen."," ")),n("app-video",{key:"e3aed1d940a09e29469258582808793e448dc192",class:"unrestricted-width",style:{maxWidth:"1000px",margin:"0 auto",padding:"1rem"}},n("geov-yasgui",{key:"cf119f5de0e0f90061f4447d6c185eaeaeedd5e8",queryTabs:t,style:{background:"#fff",height:"400px"},plugins:new Set(["mapCircles"]),defaultPlugin:"mapCircles",collapse:true,hideYasqueToggle:true,pluginConfig:{mapCircles:{disableScrollZoom:true,displayMapNavigationControls:true,maxZoom:25,radiusMin:4,radiusMax:25}}}),n("div",{key:"d19d8170340c6b55dd58a2238e9bb739a1ef3d58",class:"restricted-width",slot:"caption"},"Karte basierend auf"," ",n("a",{key:"045923bb4759bec7831cac6f72c3a24293b36a0f",href:"https://www.geovistory.org/project/1719422",target:"_blank",rel:"noopener noreferrer"},"«Jüdische & katholische Zentren Basels»"),", umgesetzt mit Geovistory.")),n("div",{key:"1a51aa1591d07c12bb4543c34beeb51d3c94f482",class:"ion-padding-start ion-padding-end ion-padding-bottom"},n("p",{key:"0ab479c4350aa48000f690f098984bd7cfa600c1"},"Die dunkelblauen Punkte auf der Karte zeigen die Wohnorte der Gründungsmitglieder des israelitischen Männervereins Dowor Tow (1857). Die hellblauen Punkte zeigen die Wohnorte von jüdischen Handwerkerinnen und Handwerker um 1910 anhand einer Liste, die der jüdische Frauenverein erstellt hatte. Im Vergleich zeigt sich, dass sich um 1910 Wohnorte zusätzlich zur Altstadt auch im Hegenheimerquartier ballten. Nur vereinzelt lebten Personen hingegen in Kleinbasel."),n("h2",{key:"0d34d171abbf7b3ca55c6a4da2ab7420d8d23501"},"Rätsel"),n("p",{key:"ffaa18bbc383ef14275a2c4897215b73d7f3cdd1"},"Maurice Goldstein wohnte um 1910 am Bläsiringweg 15 in Kleinbasel. Welchen Beruf übte er aus?"),n("p",{key:"7ea3b24d1adbed7acdcde995f290c7c5bbf20434"},n("ion-button",{key:"0648a239987da09592b8cd3fc485ad9fe2757d39",color:"primary",onClick:e=>this.successModal.open()},"Gummischuhhändler"),n("ion-button",{key:"6eb5acb6c2050e1ef4046b03e9900ceb212fb69a",color:"primary",onClick:e=>this.failModal.open()},"Uhrenmacher"),n("ion-button",{key:"6ae317586c158a44248e20a1e70d15694c930d0e",color:"primary",onClick:e=>this.failModal.open()},"Herrenschneider")),n("app-literatur",{key:"46d998717e9e5dc3471ae92f50dca079a379336c",items:[n(a,null,"Gerber, Brigitta: «Nicht aber für den Fall einer etwaigen Verheiratung mit einem Nichtberechtigten»! – Arbeits- und Niederlassungsbewilligungen mit Vorbehalten, in: Verein Frauenstadtrundgang Basel (Hg.): Geschichten aus der Empore: auf den Spuren jüdischer Frauen in Basel, Basel 1999, S. 79-95."),n(a,null,"Haumann, Heiko (Hg.): Acht Jahrhunderte Juden in Basel, Basel 2005."),n(a,null,"Kury, Patrick: Ostjudenmigration nach Basel, 1890-1930 : «Man akzeptierte uns nicht, man tolerierte uns!» Lizenziatsarbeit, Universität Basel, Basel 1994."),n(a,null,"Kury, Patrick: «Fremd und rückständig»: Ostjuden in Basel um 1900, in: Haumann, Heiko (Hg.): Der Erste Zionistenkongress von 1897 – Ursachen, Bedeutung, Aktualität, Basel 1997, S. 197-201.")]}))),n("app-dialog-fail",{key:"f82f66dc27fb4a1f78f8342f58f4d3e07612ab92",ref:e=>this.failModal=e,titleTxt:"Leider falsch"},n("p",{key:"3fdcacc156e9e5eeffd705e028324c06a755bec0"},"Die richtige Antwort ist «Gummischuhhändler»")),n("app-dialog-success",{key:"30609b65aa82c23f016054a6ac64edc049e3f587",nr:d,ref:e=>this.successModal=e},n("ion-img",{key:"7bc18eead17c0099a38a7545a76e69603b91b09b",src:i("../../assets/hurray.gif")}),n("div",{key:"d01808fef0d89cb709c7e96e7fb103a865e93d70",class:"ion-padding"},"Du hast Kapitel «",r[d],"» erfolgreich abgeschlossen !!")))}};o.style=s;export{o as page_teil_10};
