import{r as e,h as n,d as a,F as d}from"./p-D6EONAzC.js";import{t as i}from"./p-DnVJQNAc.js";const r=":host{display:block}";const s=2;const c=class{constructor(n){e(this,n)}render(){return n(d,{key:"57e34aea82b52e10bb86753c207e4af9f46214dc"},n("app-header",{key:"2aabf2ea1ea29778eee107bc8a9d14af1ce947e1",headerTitle:i[s]}),n("ion-content",{key:"6b0d412a92a5fe10a9431d2e97c95f95344fbab9",class:"ion-padding",id:"main-menu"},n("app-content",{key:"ccb9c4db880b67ee30a15ab0f92a09ae24baae77"},n("h1",{key:"ad1ef2a5d4c640d38a494b5c1b72ec8d7faad6e1",class:"ion-hide-md-down"},"Jüdisches Leben in Basel"),n("p",{key:"8d665142bcbacfd32273aa9da24ec126a587afe1",class:"lead"},"Drei jüdische Gemeinden in 800 Jahren"),n("p",{key:"e78338ed4a184614546dd4bdf03b56bdd03bd5ab"},"In den letzten 800 Jahren gab es in Basel drei jüdische Gemeinden. Nach der Auslöschung der ersten Gemeinde durch ein Pogrom und dem schnellen Verschwinden einer zweiten im Mittelalter bildete sich zu Beginn des 19. Jahrhunderts eine dritte jüdische Gemeinde. Diese besteht bis heute."," "),n("p",{key:"fe96eeac83faf4af85b8e2ff8eef4c57526c856d"},"Die Mitglieder der ersten jüdischen Gemeinde wohnten zwischen Barfüsserplatz und Marktplatz. Viele arbeiteten im Geldwechsel- und Kreditgeschäft, dessen Ausübung den Christen verboten war. Ein Geschäftspartner war etwa Bischof Heinrich von Thun, der 1223 den Münsterschatz als Sicherheit für ein Darlehen gab, mit dem er wohl den Bau der Mittleren Brücke finanzieren konnte. Die Synagoge der ersten Gemeinde befand sich an der Gerbergasse, der Friedhof lag beim Kollegiengebäude der Universität Basel am Petersplatz. Als eine Pestwelle Europa erfasste, wurde die jüdische Bevölkerung zum Sündenbock gemacht. Auch Basel ging gewalttätig gegen die jüdischen Mitmenschen vor. Während der Fasnachtszeit, im Januar 1349, wurden alle Jüdinnen und Juden, die nicht geflohen waren, auf einer Insel im Rhein verbrannt."," "),n("p",{key:"fa66b3711024ef021390da57b6958c94b5c007ed"},"Ein halbes Jahrhundert später erhielt Basel eine zweite jüdische Gemeinde mit einer Synagoge an der Grünpfahlgasse. Bereits nach wenigen Jahren verschwand diese aber wieder. Eine offizielle Verfolgung oder Ausweisung war nicht der Grund. Vielmehr floh die jüdische Bevölkerung wohl aus der Stadt, weil sie sich nicht sicher fühlten und eine Verfolgung fürchteten. Denn Bruno von Rappoltstein, Herrscher von Ribeauvillé, hatte 1397 Jüdinnen und Juden unter Folter gezwungen, das Vergiften von Brunnen zu gestehen. Dabei war auch der Name eines Basler Juden genannt worden."," "),n("app-video",{key:"fbdc24ac1da6b5dbd49e82789d4404e08c51a284"},n("iframe",{key:"b9cfdf579464a43df563ff8cb96c10ef657ae89b",width:"100%",height:"100%",style:{"aspect-ratio":"16/9"},src:"https://www.srf.ch/play/embed?urn=urn:srf:video:00f8e25c-099a-4dcf-b6aa-49495936c76c&subdivisions=false",allowFullScreen:true,allow:"geolocation *; autoplay; encrypted-media"}),n("span",{key:"a437a32a65346c3f4f68d2724d2cb5323f14fcd9",slot:"caption"},"«Schweiz Aktuell» des Schweizer Fernsehen vom 28. August 1997")),n("p",{key:"2be609b45ebc451884325e5a808c003a00f85c5c"},"Das Schweizer Fernsehen berichtete in der Sendung «Schweiz Aktuell» von 28. August 1997 über das Leben der Juden in Basel. Darin wird auch beschrieben, wo sich heute einzelne Grabsteine des Friedhofs der ersten jüdischen Gemeinde befinden."," "),n("h2",{key:"a2bca22c222ea3aad35259ed62cbd23b029a65c4"},"Rätsel"),n("p",{key:"fea1cf0669f87f89a192d6533a3a721d1c00d4bd"},"In welchem Museum können die Grabsteine besichtigt werden?"),n("p",{key:"6b8faacd147d78d19d44472e3b750ecbcf0be8fb"},n("ion-button",{key:"8b759e3d2fee3468ef03e5c06fabb2bbdab2da86",color:"primary",onClick:e=>this.failModal.open()},"Historisches Museum Basel"),n("ion-button",{key:"dade3164e2ed671c8af6be256dbbd25973340d9a",color:"primary",onClick:e=>this.failModal.open()},"Museum der Kulturen Basel"),n("ion-button",{key:"21c63ea75c40e9bf4ef783b8ce8315b14b258ba2",color:"primary",onClick:e=>this.successModal.open()},"Jüdisches Museum der Schweiz")),n("app-literatur",{key:"8a02a5620ac8a76ef4210c9d91c08ac6cee3174e",items:[n(d,null,"Haumann, Heiko: Juden in Basel und Umgebung – Zur Geschichte einer Minderheit. Darstellung und Quellen für den Gebrauch an Schulen, Herausgegeben von den Regierungsräten der Kantone Basel-Stadt und Basel-Landschaft, Basel 1999."),n(d,null,"Haumann, Heiko (Hg.): Acht Jahrhunderte Juden in Basel, Basel 2005.")]}))),n("app-dialog-fail",{key:"9e8a792bee537d3eebcdb2e1b4cd254c16fc33de",ref:e=>this.failModal=e,titleTxt:"Leider falsch"},n("p",{key:"8617137da7be8070a7e61d6e0d919aff3108c862"},"Die richtige Antwort ist «Jüdisches Museum der Schweiz»")),n("app-dialog-success",{key:"d9597c834fa1ebbeb5d147be95b6fa9e25a26a54",nr:s,ref:e=>this.successModal=e},n("ion-img",{key:"bf975238041250b6f9d42569a576fe8bf6c20fcd",src:a("../../assets/hurray.gif")}),n("div",{key:"6f9d9501cb460c31451e1c532dbe86304eefab2e",class:"ion-padding"},"Du hast Kapitel «",i[s],"» erfolgreich abgeschlossen !!")))}};c.style=r;export{c as page_teil_2};
