import{r as e,h as i,d as n,F as r}from"./p-8Q-8j7RM.js";import{t as a}from"./p-DnVJQNAc.js";const t=":host{display:block}";const s=7;const d=class{constructor(i){e(this,i)}render(){return i(r,{key:"338dd509750aaa9aff2aa7bc8f436fe66b28d071"},i("app-header",{key:"a02d6ed881014ce384d115c9ea58024cf95f24d7",headerTitle:a[s]}),i("ion-content",{key:"29f61e32e2dad912740d8903e414203111ba9a31",class:"ion-padding",id:"main-menu"},i("app-content",{key:"7d82a69e27bb3157f8848a2ba8a8e39f2bf651e6"},i("h1",{key:"26fcbe68596a8d24a4591de455202349d8e5cfce",class:"ion-hide-md-down"},a[s]),i("p",{key:"b1dd692bf8de3059258b4308cc27229c14aba2c4",class:"lead"},"Lehrstück erfolgreicher politischer Arbeit:",i("br",{key:"aa81ff8f5c135008d7d99deb46f413ffbf14a941"}),"Die Basler Juden erhalten einen eigenen Friedhof"),i("p",{key:"774e537745a0b6c00861c82674f530b46677d99a"},"Rund hundert Jahre lang bestattete die jüdische Gemeinde von Basel ihre Toten im benachbarten Hégenheim im Elsass. Dort ist seit 1673 ein jüdischer Friedhof in Betrieb. Der Wunsch nach einem eigenen Friedhof auf Basler Boden verstärkte sich, als sich die Zusammensetzung der jüdischen Gemeinde durch Zuwanderung veränderte und nicht mehr ausschliesslich elsässisch geprägt war. Ausserdem vergrösserte sich der bürokratische und finanzielle Aufwand für eine Bestattung im Ausland."," "),i("app-img",{key:"501c7c134508aaec3a26fa6b34e6963942119631",src:"../../assets/teil-7/friedhof-1.jpg"},"Auf dem jüdischen Friedhof in Hégenheim wurden bis 1903 auch Basler Jüdinnen und Juden begraben.",i("br",{key:"2afe04c2c352c7216be877bb967e2e0114c1e20f"})," Bild zvg"),i("p",{key:"d87be1b852568d183472664dcb61ae4874f82b84"},"Bis 1903 der eigene Friedhof an der heutigen Theodor Herzl-Strasse in Betrieb genommen werden konnte, war ein langer Weg politischer Arbeit nötig. Zwei Petitionen an den Regierungsrat wurden 1878 und 1888 mit der Begründung abgelehnt, dass die öffentlichen Friedhöfe auch den Juden zur Verfügung stünden und für das Bestattungswesen in Basel die Gesundheitsbehörden zuständig seien. Eine Benutzung der öffentlichen Friedhöfe war den Juden aber aus religiösen Gründen nicht möglich. Das Verbot, ein Grab zu öffnen und damit die Ruhe der Toten zu stören, ist ein zentraler Aspekt der jüdischen Glaubenslehre. Gräber auf öffentlichen Friedhöfen hingegen konnten nach einigen Jahren aufgehoben werden."),i("app-img",{key:"bed2ec58f2710765104f6964427a79bf89e20388",style:{maxWidth:"450px"},src:"../../assets/teil-7/friedhof-2.jpg"},"Grossrat Nathan Höchstetter-Levi starb 1909 und erhielt als einer der ersten einen Grabstein auf dem neuen jüdischen Friedhof in Basel.",i("br",{key:"16d6c6d67730000e4c3bb387883f6c0d34efa30c"})," Bild zvg"),i("p",{key:"ab53cb9fdd09d6bad9a3f3ee23ae0bf566bceebb"},"Die jüdische Gemeinde hatte aus den abgelehnten Petitionen ihre Lehren gezogen. 1901 startete ein Initiativkomitee mit Präsident Salomon Bloch-Roos einen neuen Anlauf. Das Komitee engagierte mit dem Rechtsanwalt und Politiker Emil Göttisheim einen Berater, der bei der Ausarbeitung einer neuen Petition mithalf. Im Vorfeld wurden umfangreiche rechtliche Abklärungen getroffen, mögliche Standorte des Friedhofs evaluiert und zahlreiche Gespräche mit dem zuständigen Sanitätsdepartement und weiteren Personen geführt. Das sorgfältige Vorgehen zahlte sich aus. Die Regierung arbeitete einen Ratschlag aus, in dem sie die Genehmigung einer Ausnahmebewilligung für einen eigenen Friedhof befürwortete. Als sich im Grossen Rat eine kritische Haltung insbesondere bei den Freisinnigen abzeichnete, verschickte das Initiativkomitee ein Schreiben an alle Grossräte und organisierte eine Informationsveranstaltung für die freisinnigen Grossräte. Referent Emil Göttisheim, selber freisinniger Grossrat, konnte die Anwesenden überzeugen."),i("p",{key:"10a1b03633e0560670ebd2251b61b1ef9d16da4c"},"In der Grossratssitzung vom 10. April 1902 war das Geschäft deshalb schnell behandelt und wurde ohne grosse Diskussion angenommen. Die Grossräte gewichteten die Religionsfreiheit höher als die strikte Einhaltung des Gesetzes, das dem Staat die alleinige Hoheit über das Bestattungswesen vorschrieb."),i("h2",{key:"a349232150f36a154392e527bba8b1c047b62741"},"Rätsel"),i("p",{key:"cb92a18a7b66feaa29c91530e38e71741c83cde9"},"Die Basler Chronik vermerkt den Tod von Nathan Höchstetter"," ",i("a",{key:"a9ce3898ad99014813dc28ebec16e2bffc8d0b65",href:"https://www.baslerstadtbuch.ch/chronik/1909/06/07/es-stirbt-nathan-hoechstetter-levy-der-eine-reihe-von-jahren-die-sozialdemokratische-partei-im.html",target:"_blank",rel:"noopener noreferrer"},"hier.")," ","Für welche Partei sass Nathan Höchstetter mehrere Jahre im Grossen Rat?"),i("p",{key:"3ed213bbe7c003894128d0840a8f4185c128b2b8"},i("ion-button",{key:"c67d79c327944da081d90b6ca286271ae80a0609",color:"primary",onClick:e=>this.failModal.open()},"Freisinnig-Demokratische Partei"),i("ion-button",{key:"a391208a48b9f391a662d1f024a3fc6d4df1729e",color:"primary",onClick:e=>this.failModal.open()},"Liberale Partei"),i("ion-button",{key:"3206a2f32eceb576ca8807ec62032fb187e7fce8",color:"primary",onClick:e=>this.successModal.open()},"Sozialdemokratische Partei")),i("app-literatur",{key:"09043b18b031d5f826d545569363eff68853b392",items:[i(r,null,"Bloch-Roos, Salomon: Wie die israelitische Gemeinde in Basel zu einem eigenen Friedhof gekommen ist. Eine Berichterstattung. Basel 1902."),i(r,null,"Draeger, Urs: Die Israelitische Gemeinde Basel 1880-1933: soziale Struktur, Organisation und Konflikte einer Einheitsgemeinde. Lizentiatsarbeit, Universität Freiburg/Schweiz, Freiburg 2001.")]}))),i("app-dialog-fail",{key:"e6c9e3d819cf98e9072997238b234cf9301f2abd",ref:e=>this.failModal=e,titleTxt:"Leider falsch"},i("p",{key:"0b8d134f3e13c9667ba8bef6543d04c8d6643988"},"Die richtige Antwort ist «Sozialdemokratische Partei»")),i("app-dialog-success",{key:"6ad38c74cdf16f1c76c1030a26637117948772c9",nr:s,ref:e=>this.successModal=e},i("ion-img",{key:"8fac401ad9d64163a525141e296d231dcb5b1f75",src:n("../../assets/hurray.gif")}),i("div",{key:"ce791e44b3f9e64b7c2d270cd494a4e205638dad",class:"ion-padding"},"Du hast Kapitel «",a[s],"» erfolgreich abgeschlossen !!")))}};d.style=t;export{d as page_teil_7};
